/* eslint-disable */
import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

  //var a, b = [10, 100]; //ES6 destructuring 문법 

  let [글제목,글제목변경] = useState(['남자 코트 추천', '강남 우동 추천','남자 여름 코디']); 

  //state라는 변수 저장공간 만들기
  //이렇게 State를 만들면 array가 생성된다.
  //array안에는 [a,b]처럼 두가지 데이터가 들어있다.
  //a에는 '남자 코트 추천'이 들어있고 
  //b에는 a의 데이터를 수정하기 위한 함수가 들어있다. 
  //state는 변수 대신 쓰는 데이터 저장 공간 
  //useState()를 사용해서 만들어야 함
  //문자, 숫자, array, Object 다 저장 가능
  //state를 굳이 쓰는 이유는? 
  //react를 Web-app 처럼 만들고 싶으면 전부 state로 만들어라
  //데이터 변경시에 state로 만들어진 데이터라면 HTML이 저절로 재렌더링이 되므로
  //state로 만든 변수들이 엄청 용이하다. 

  let posts = '강남 고기 맛집';
  return (
    <div className="App">
      <div className="black-nav">
        <div>개발 blog</div>
      </div>
      <div className="list">
        <h3>{ 글제목[0] } <span>👍</span> 0 </h3>
        <p>2월 17일 발행</p>
        <hr/>
      </div>
      <div className="list">
        <h3>{ 글제목[1] }</h3>
        <p>2월 18일 발행</p>
        <hr/>
      </div>
      <div className="list">
        <h3>{ 글제목[2] }</h3>
        <p>2월 19일 발행</p>
        <hr/>
      </div>
    </div>
  );
}

export default App;
