import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

  //var a, b = [10, 100]; //ES6 destructuring 문법 

  let [글제목,글제목변경] = useState('남자 코트 추천'); //state라는 변수 저장공간 만들기
  //이렇게 State를 만들면 array가 생성된다.
  //array안에는 [a,b]처럼 두가지 데이터가 들어있다.
  //a에는 '남자 코트 추천'이 들어있고 
  //b에는 a의 데이터를 수정하기 위한 함수가 들어있다. 

  let posts = '강남 고기 맛집';
  return (
    <div className="App">
      <div className="black-nav">
        <div>개발 blog</div>
      </div>
      <div className="list">
        <h3>{ 글제목 }</h3>
        <p>2월 17일 발행</p>
        <hr/>
      </div>
    </div>
  );
}

export default App;
