import { Button,NavDropdown, Nav,Navbar,Container, Jumbotron } from 'react-bootstrap';
import logo from './logo.svg';
import React, {useState} from 'react';
import './App.css';
import Data from './data.js';

//내보내기 : export default 변수명
//가져오기 : import 변수명 from 경로 

function App() {

  let [shoes, shoes변경] = useState(Data);
  return (
    <div className="App">
      <Navbar bg="light" expand="lg">
      <Container>
        <Navbar.Brand href="#home">Shoe Shop</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            <Nav.Link href="#link">Link</Nav.Link>
            <NavDropdown title="Dropdown" id="basic-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Container>
      </Navbar>

      <Jumbotron className="background">
        <h1>20% Season Off!</h1>
        <p>
          This is a simple hero unit, a simple jumbotron-style component for calling
          extra attention to featured content or information.
        </p>
        <p>
          <Button variant="primary">Learn more</Button>
        </p>
      </Jumbotron>

      <div className="container">
        <div className="row">
          {
            shoes.map(function(a){
              return (
                <Card shoes={shoes}></Card>
              )
            })
          }
        </div>
      </div>

    </div>
  );
}

function Card(props){
  return(
    <div className="col-md 4">
      <img src="https://codingapple1.github.io/shop/shoes1.jpg" width="100%"></img>
      <h4>{ props.shoes[0].title }</h4>
      <p>{ props.shoes[0].content } & {props.shoes[0].price}</p>
    </div>
  )
}

export default App;
