import React, {useState} from 'react';
import { useHistory, useParams } from 'react-router-dom';
import styled from 'styled-components';
import './Detail.scss';

//아래와 같이 CSS를 미리 입혀 놓은 컴포넌트를 만들 수 있음 (styled import후)
let 박스 = styled.div`
    padding: 20px;
    
`;

let 제목 = styled.h4`
    font-size: 25px;
    color: ${ props => props.색상 }
`;

// 컴포넌트의 Lifecycle
// 컴포넌트는 등장, 업데이트(재렌더링), 퇴장(사라짐)의 라이프사이클을 가진다. 
// 여기서 Hook이라는 것으로 컴포넌트의 인생 중간중간에 뭔가 명령을 줄 수 있다. 
// ex.<Detail>등장 전에 이것좀 해주세요 등등

class Detail2 extends React.Component{

    componentDidMount(){
    //얘네가 Hook이다. 
    }
    componentWillUnmount(){

    }

}







function Detail(props){

    let { id } = useParams();
    let 찾은상품 = props.shoes.find((상품)=>{
        return 상품.id == id;
    })
    let history = useHistory();
    
    return (
        <div className="container">
          <박스>
            <제목 className="red" 색상='green'>Detailed</제목>
          </박스>

          <div className="my-alert2">
              <p>재고가 얼마 남지 않았습니다</p>
          </div>

          <div className="row">
            <div className="col-md-6">
              <img src={'https://codingapple1.github.io/shop/shoes' + (찾은상품.id + 1) + '.jpg'} width="100%" />
            </div>
            <div className="col-md-6 mt-4">
              <h4 className="pt-5">{찾은상품.title}</h4>
              <p>{찾은상품.content}</p>
              <p>{찾은상품.price}</p>
              <button className="btn btn-danger">주문하기</button> 
              <button className="btn btn-danger" onClick={
                ()=>{ history.goBack();
                    //history.push('/xxx')이라 하면 특정 경로로 이동시킬 수 있다.
                }}>뒤로가기</button>
            </div>
          </div>
        </div> 
    )
}

export default Detail;
